import React, { useState, useEffect, useMemo } from "react";
import {
  useCSVReader,
  lightenDarkenColor,
  formatFileSize,
} from "react-papaparse";
// import "../Styles/FileInputForm.css";
import "bootstrap/dist/css/bootstrap.min.css";
import ReactTable from "./ReactTable";

const DEFAULT_REMOVE_HOVER_COLOR = "#A01919";
const REMOVE_HOVER_COLOR_LIGHT = lightenDarkenColor(
  DEFAULT_REMOVE_HOVER_COLOR,
  40
);

const FileInputForm = ({ updatedRows, handleColumns }) => {
  const { CSVReader } = useCSVReader();
  const [csvData, setCsvData] = useState({});
  const [zoneHover, setZoneHover] = useState(false);
  const [columnData, setColumnData] = useState([]); //headers
  const [rowData, setRowData] = useState([]);   //data
  const [removeHoverColor, setRemoveHoverColor] = useState(
    DEFAULT_REMOVE_HOVER_COLOR
  );

  const columns = useMemo(() => columnData, [columnData]);  //update columns

  const rows = useMemo(() => rowData, [rowData]);  //update rows

  // useEffect(() => {
  //   if (updatedRows.length > 0) {
  //     console.log(updatedRows);
  //     setRowData(updatedRows);
  //   }
  // }, [updatedRows]);

  useEffect(() => {
    if (Object.keys(csvData).length > 0) {
      const columns = csvData?.data[0]?.map((col, index) => {
        return {
          Header: col.trim(),
          accessor: col.trim().split(" ").join().toLowerCase(),
        };
      });

      const rows = csvData.data.slice(1).map((row) => {
        return row.reduce((acc, curr, index) => {
          acc[columns[index].accessor] = curr;
          return acc;
        }, {});
      });
      // handleColumns({ columns: columns, rows: rows });
      setColumnData(columns);
      setRowData(rows);
    }
  }, [csvData]);

  const handleFileUpload = (results) => {
    setZoneHover(false);
    setCsvData(results);
  };
  return (
    <CSVReader
      onUploadAccepted={handleFileUpload}
      onDragOver={(event) => {
        event.preventDefault();
        setZoneHover(true);
      }}
      onDragLeave={(event) => {
        event.preventDefault();
        setZoneHover(false);
      }}
    >
      {({
        getRootProps,
        acceptedFile,
        ProgressBar,
        getRemoveFileProps,
        Remove,
      }) => (
        <>
          <div
            {...getRootProps()}
            className='zone'
          >
            {acceptedFile ? (
              <>
                <div className='file'>
                  <div className='info'>
                    <span className='size'>
                      {formatFileSize(acceptedFile.size)}
                    </span>
                    <span className='name'>{acceptedFile.name}</span>
                  </div>
                  <div className='ProgressBar'>
                    <ProgressBar/>
                  </div>
                  <div
                    {...getRemoveFileProps()}
                    className='remove'
                    onMouseOver={(event) => {
                      event.preventDefault();
                      setRemoveHoverColor(REMOVE_HOVER_COLOR_LIGHT);
                    }}
                    onMouseOut={(event) => {
                      event.preventDefault();
                      setRemoveHoverColor(DEFAULT_REMOVE_HOVER_COLOR);
                    }}
                  >
                    <Remove color={removeHoverColor} />
                  </div>
                </div>
              </>
            ) : (
              "Click to upload"
            )}
          </div>
          <div>
            <ReactTable rowData={rowData} columnData={columnData}/>
          </div>
        </>
      )}
    </CSVReader>
  );
};

export default FileInputForm;
