import React,{useCallback, useRef, useState} from 'react'
import { useCSVReader } from 'react-papaparse';
import {useDropzone} from 'react-dropzone';



  


const Upload = () => {

    const buttonRef = useRef(null);
    const handleOnFileLoad = (data) => {
        console.log(data);
    };
    const onErrorHandler = (err,file,inputElem,reason) => {
        console.log(err)
    };
    const handleFileRemove=(data) => {
        console.log(data);
    }
    const handleOpenCSVReader = (e) =>{
        if(buttonRef.current){
            buttonRef.current.onClick(e);  
        }
    }
    const {CSVReader} = useCSVReader();
  return (
<div class="card w-75">
  <div class="card-body">
    <section className="container">
        <CSVReader ref={buttonRef} onFieldLoad={handleOnFileLoad}
        onError={onErrorHandler}
        onClick
        onRemoveFile={handleFileRemove}
        >
          {({file}) => (
          <div class="d-inline-flex p-2 bd-highlight">
                <button type='button' onClick={handleOpenCSVReader}>Upload File</button>
          </div>)}
        </CSVReader>
    </section>
      </div>
    </div>
  );
}

export default Upload
