import React, { useState } from "react";
import {
  FormControl,
  Grid,
  InputLabel,
  MenuItem,
  Select,
  Typography,
} from "@mui/material";
import Main from "./Main";

const Dashboard = () => {
  const [rows, setRows] = useState([]);
  const [updatedRow, setUpdatedRow] = useState([]);
  const [columns, setColumns] = useState([]);
  const [selectedColumn, setSelectedColumn] = useState("");
  const [selectedFormat, setSelectedFormat] = useState("");

  const handleColumns = ({ columns, rows }) => {
    setColumns(columns);
    setRows(rows);
  };

  const handleFormat = (e) => {
    setSelectedFormat(e.target.value);

    let res = [];

    if (selectedColumn === "date") {
      if (selectedColumn === "Total") {
        res = rows.filter(
          (row) =>
            row[selectedColumn] &&
            (row[selectedColumn] = new Intl.NumberFormat("en-IN", {
              maximumSignificantDigits: 3,
              style: "currency",
              currency: e.target.value,
            }).format(row[selectedColumn]))
        );
      }
    } 
    setUpdatedRow(res);
  };

  return (
    <div style={{ marginTop: "50px" }}>
      <Grid
        container
        spacing={2}
      >
        <Grid
          item
          sm={6}
          xs={6}
        >
          <div style={{ margin: "1rem" }}>
            <Typography
              variant='h6'
              style={{ margin: "1rem 0" }}
            >
              Actions
            </Typography>
            <FormControl fullWidth>
              <InputLabel id='demo-simple-select-label'>
                Select Column
              </InputLabel>
              <Select
                labelId='demo-simple-select-label'
                id='demo-simple-select'
                value={selectedColumn}
                label='Select Column'
                onChange={(e) => setSelectedColumn(e.target.value)}
              >
                {columns.map((column) => (
                  <MenuItem value={column.accessor}>{column.Header}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </div>
          <div style={{ margin: "1rem" }}>
            <FormControl fullWidth>
              <InputLabel id='demo-simple-select-label'>
                Format Options
              </InputLabel>
              <Select
                labelId='demo-simple-select-label'
                id='demo-simple-select'
                value={selectedFormat}
                label='Format Options'
                onChange={handleFormat}
              >
                {config[selectedColumn]?.map((val) => (
                  <MenuItem value={val}>{val}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </div>
        </Grid>
        <Grid
          item
          sm={6}
          xs={6}
        >
          <Main
            handleColumns={handleColumns}
            updatedRows={updatedRow}
          />
        </Grid>
      </Grid>
    </div>
  );
};

export default Dashboard;
