        onClick
        onRemoveFile={handleFileRemove}
        >
          {({file}) => (
          <div class="d-inline-flex p-2 bd-highlight">
                <a className='btn btn-outline-primary'>Upload File</a>
          </div>)}
        </useCSVReader>
    </section>
      </div>
    </div>
  );
}

export default Upload
